function getTeams(){$.getJSON("json/teams.json",function(e){var s=e.teams.length;$.each(e.teams,function(s){var a=e.teams[s].number;$("#element_"+a).addClass("team");var t=parseInt($("#element_"+a).attr("data-x")),n=parseInt($("#element_"+a).attr("data-y"));if(t<=540)var m="<div class='detail left'><h1>"+e.teams[s].name+"</h1><hr/><h2>Description &amp; Mission</h2><p>"+e.teams[s].desc+"</p><hr/><h2>KPIs</h2><p>"+e.teams[s].kpis.join(", ")+"</p><hr/><h2>Lead</h2><p>"+e.teams[s].lead+"</p><hr/><h2>Members</h2><p>"+e.teams[s].members.join(", ")+"</p></div>";if(t>=540)var m="<div class='detail right'><h1>"+e.teams[s].name+"</h1><hr/><h2>Description &amp; Mission</h2><p>"+e.teams[s].desc+"</p><hr/><h2>KPIs</h2><p>"+e.teams[s].kpis.join(", ")+"</p><hr/><h2>Lead</h2><p>"+e.teams[s].lead+"</p><hr/><h2>Members</h2><p>"+e.teams[s].members.join(", ")+"</p></div>";$("#element_"+a).append(m)})})}$(document).ready(function(){$.getJSON("json/elements.json",function(e){var s=e.elements.length;$.each(e.elements,function(a){var t=e.elements[a].number,n=e.elements[a].symbol,m=e.elements[a].name,p=e.elements[a].atomic_mass,i=60*(e.elements[a].xpos-1),l=65*(e.elements[a].ypos-1),h="<div class='element' data-x="+i+" data-y="+l+" id='element_"+t+"'><span class='number'>"+t+"</span><span class='symbol'>"+n+"</span><span class='name'>"+m+"</span><span class='weight'>"+p+"</span></div>";$(".table").append(h),$("#element_"+t).css("transform","translate("+i+"px,"+l+"px)").css("opacity",1),0===--s&&getTeams()})})});